#first attempt, could be improved.
def complement_string(s):
    complement = []
    for dna_nucleotide in list(s):
        if dna_nucleotide.upper() == "G":
            dna_nucleotide = "C"
        elif dna_nucleotide.upper() == "A":
            dna_nucleotide = "T"
        elif dna_nucleotide.upper() == "T":
            dna_nucleotide = "A"
        elif dna_nucleotide.upper() == "C":
            dna_nucleotide = "G"
        complement.append(dna_nucleotide)
    
    return ''.join(complement[::-1])


 #with a dictionary
def complement_string_2(s):
    complementary_base_dict = {'A':'T', 'T':'A','C':'G','G':'C'}
    complement = [complementary_base_dict.get(nucleotide) for nucleotide in s]
    return ''.join(complement[::-1])

